// create data series array
float[] testValues = new float[128];
// fill data series
for ( int i = 0; i < 128; i++ )
{
    testValues[i] = Math.Sin( i / 18.0 * Math.PI );
}
// add new waveform to the chart
chart.AddWaveform( "Test", Color.DarkGreen, 3 );
// update the chart
chart.UpdateWaveform( "Test", testValues );
